
public class ICity {

  private String name;
  private Double distance;
  
  public ICity(String name, Double distance) {
    this.name = name;
    this.distance = distance;
  }
 
  
  public Double getDistance() {
    return this.distance;
  }

  public String getName() {
    // TODO Auto-generated method stub
    return this.name;
  }
}
